# 1

def has_repeats(id):
	d = False
	t = False
	for l in id:
		c = id.count(l)
		if c == 2: d = True
		if c == 3: t = True
	return d, t

doubles = []
triples = []
for id in ls:
	d, t = has_repeats(id)
	if d: doubles += [id]
	if t: triples += [id]

print(len(doubles) * len(triples))

# 2

def are_close(s1, s2):
	dif = False
	for i in range(len(s1)):
		if s1[i] != s2[i]:
			if dif: return False
			else: dif = True
	return True

def find_boxes(ls):
	for i in range(len(ls)):
		id1 = ls[i]
		for id2 in ls[i+1:]:
			if are_close(id1, id2):
				return id1, id2

b1, b2 = find_boxes(ls)
for i in range(len(b1)):
	if b1[i] != b2[i]:
		print(b1[:i] + b1[i+1:])
		break
